import React, {useState}from 'react'
import { isAuthenticated } from '../auth/helper'
import Base from "../core/Base"
import {Link} from "react-router-dom"
import {createCategory} from "./helper/adminapicall"


export default function AddCategory() {

    const [name, setName] = useState("")
    const [success, setSuccess] = useState(false)
    const [error, setError] = useState(false)

    const {user, token} = isAuthenticated();

    const goBack =()=>{
        return(
            <div className="mt-5">
                <Link className="btn btn-sm btn-info mb-3" to="/admin/dashboard">Admin Home</Link>
            </div>
        )
    }

    const handleChange = (event) => {
        setError("");
        setName(event.target.value)
        

    }
    const onSubmit = (event)=>{
        event.prevetnDefault();
        setError("");
        setSuccess(false);

        createCategory(user._id, token,{name})
        .then(data=>{
            if(data.error){
                setError(data.error)
            }else{
                setError("")
                setSuccess(true)
            }
        })
    }
    
    const myCategoryForm = ()=>(
        <form>
            <div className="form-group">
                <h1 className="lead">Enter Category</h1>
                <input type="text"
                className="form-control my-3"
                autoFocus
                onChange={handleChange}
                value={name}
                required
                placeholder="ex. Summer"/>
                <button onClick={onSubmit} className="btn btn-outline-info">Create Category</button>
            </div>
        </form>
    )

    return (
        <Base title="Create a category here"
        description="Add a new category  for new tshirts"
        className="container bg-info p-4"
        >
         <div className="row bg-white rounded">
            <div className="col-md-8 offset-md-2">
                {myCategoryForm()}
                {goBack()}
                <p className>{name}</p>
            </div>
         </div>
         
        </Base>
    )
}
