import React, { useState } from "react"
import Base from "../core/Base"
import { Link , Redirect} from "react-router-dom"
import {signin, authenticate, isAuthenticated} from "../auth/helper"


const SignIn = () => {

    const [values, setValues] = useState({
        email: "",
        password: "",
        loading:  false,
        didRedirect: false
    })

    const {email, password, error, loading, didRedirect} = values
    const {user}=isAuthenticated();

    const handleChange = name => event => {
        setValues({...values, error: false, [name]: event.target.value})
    }

    const onSumbit = event=> {
        event.preventDefault();
        setValues({...values, error: false, loading:true})
        signin({email,password})
        .then(data=>{
            if(data.error){
                setValues({...values, error: data.error,loading:false})
            }else{
                authenticate(data, ()=>{
                    setValues({
                        ...values,
                        didRedirect: true
                    })
                })
            }
        })
        .catch(console.log("signin request failed"))
    }
    const performRedirect = ()=>{
        if(didRedirect){
            if(user&&user.role===1){
                return <p>redirected to admin</p>
            }else{
                return <p>redirected to user </p>
            }
        }
    }
    const successMessage =()=>{
        return(
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
            <div className="alert alert-success"
            style={{display:didRedirect? "":"none"}}>
                Account Created Succesfully, Please <Link to="/signin">Login Here</Link>
            </div>
            </div>
            </div>
        )
    }
    const errorMessage =()=>{
       return( 
        <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
           <div className="alert alert-danger"
            style={{display:error? "":"none"}}>
                {error}
            </div>
            </div>
            </div>
        )
    }
    const signInForm = () => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <form>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input onChange={handleChange("email")} className="form-control" type="email" value={email} />
                        </div>
                        <div className="form-group">
                            <label className="text-light">Password</label>
                            <input onChange={handleChange("password")} className="form-control" type="password" value={password}/>
                        </div>
                        <button onClick={onSumbit} className="btn btn-success btn-block">Submit</button>
                    </form>
                </div>
            </div>
        )
    }
    return (
        <Base title="SignIn page" description="A page for user to sign In!">
            {signInForm()}
        </Base>
    )
}

export default SignIn
